import { Component } from "React";
import fetch from 'node-fetch';
import { ApolloClient } from 'apollo-client';
import { InMemoryCache } from 'apollo-cache-inmemory';
import { HttpLink } from 'apollo-link-http';
import gql from "graphql-tag";
import Head from "../components/head";
import Nav from "../components/nav";
import Terminal from "../components/terminal";
import Info from "../components/info";

const cache = new InMemoryCache();
const link = new HttpLink({
  uri: "http://localhost:4000/",
  fetch: fetch
});

const client = new ApolloClient({
  cache,
  link
});

export default class extends Component {

  constructor(props) {
    super()
    this.retrieveFields = this.retrieveFields.bind(this);
    this.state = {
      name: "",
      heroNouns: "",
      greeting: "",
      role: "",
      skills: "",
      university: "",
      degree: "",
      graduationDate: "",
      hobbyLabel: "",
      hobbyPhoto: "",
      contactMessage: "",
      contactEmail: "",
      contactLabel: "",
      contactLabel1: "",
      contactLink1: "",
      contactLabel2: "",
      contactLink2: "",
      contactLabel3: "",
      contactLink3: ""
    }
  }

  async retrieveFields() {
    client
      .query({
        query: gql`
          query {
            home {
              name
              heroNouns
              greeting
              role
              skills
              university
              degree
              graduationDate
              hobbyLabel
              hobbyPhoto
              contactMessage
              contactEmail
              contactLabel1
              contactLink1
              contactLabel2
              contactLink2
              contactLabel3
              contactLink3
            }
          }
        `
      }).then(result => {
        this.props.
        console.log(result)}
      );
  }

  componentDidMount() {
    this.retrieveFields();
  }

  render() {




    return (
      <div>
        <Head title="Dylan Bent"></Head>
        <Nav></Nav>
        <Terminal></Terminal>
        <Info></Info>
      </div>
    )
  }

}
