import { Component } from "React";
import fetch from 'node-fetch';
import { ApolloClient } from 'apollo-client';
import { InMemoryCache } from 'apollo-cache-inmemory';
import { HttpLink } from 'apollo-link-http';
import gql from "graphql-tag";
import Head from "../components/head";
import Nav from "../components/nav";
import Terminal from "../components/terminal";
import Info from "../components/info";

const cache = new InMemoryCache();
const link = new HttpLink({
  uri: "http://localhost:4000/",
  fetch: fetch
});

const client = new ApolloClient({
  cache,
  link
});

export default class extends Component {

  constructor(props) {
    super()
    this.retrieveFields = this.retrieveFields.bind(this);
    this.state = {
      home: {
        name: "",
        heroNouns: "",
        greeting: "",
        role: "",
        skills: "",
        university: "",
        degree: "",
        graduationDate: "",
        hobbyLabel: "",
        hobbyPhoto: {
          fields: {
            file: {
              url: ""
            }
          }
        },
        contactMessage: "",
        contactEmail: "",
        contactLabel: "",
        contactLabel1: "",
        contactLink1: "",
        contactLabel2: "",
        contactLink2: "",
        contactLabel3: "",
        contactLink3: ""
      }
    };
  }

  async retrieveFields() {
    console.log("retreiveFields");
    let _this = this;
    client
      .query({
        query: gql`
          query {
            home {
              name
              heroNouns
              greeting
              role
              skills
              university
              degree
              graduationDate
              hobbyLabel
              hobbyPhoto {
                fields {
                  file {
                    url
                  }
                }
              }
              contactMessage
              contactEmail
              contactLabel1
              contactLink1
              contactLabel2
              contactLink2
              contactLabel3
              contactLink3
            }
          }
        `
      }).then(result => {
        console.log(result.home.data);
        _this.setState({ home: result.data.home });
      }).catch(err => {
        console.log(err);
      });
  }

  componentDidMount() {
    this.retrieveFields();
  }

  render() {
    const home = this.state.home;
    return (
      <div>
        <Head title="Dylan Bent"></Head>
        <Nav></Nav>
        <Terminal></Terminal>
        <Info home={ home }></Info>
      </div>
    )
  }

}
